import React from "react";
import axios from "axios";

import styled from "./styled"

import {BASE_URL}  from '../../constants/urls';
import {playListToScreenBox} from './styled';




class PlayListPage extends React.Component  { 
  state ={ 
    playLists: [],
  }


  componentDidMount() { 
    this.getAllPlaylists()
  }

  getAllPlaylists = () => { 
    const url = `${BASE_URL}`
    const axiosConfig = {headers: {Authorization: "gabriel-martins-vaughan" }}
    axios
    .get(url, axiosConfig)
    .then((res) =>{
       console.log(res.data.result.list)
       this.setState({
         playLists: res.data.result.list
       })  
      })
    .catch(err => console.log(err.response))

  }

  render(){ 
       console.log('playslist',this.state.playLists)
       const playListToScreen = this.state.playLists.map( item => 

        <playListToScreenBox> 
         { item.name }
        </playListToScreenBox>)



    return( 
       <>
        <h1>PlayListPage</h1>
         <playListToScreenBox>{playListToScreen}</playListToScreenBox>
        </>
    )
  }

}

export default PlayListPage;
