import React from "react";
import axios from "axios";

import { AuthorizationUser, BASE_URL } from '../../constants/urls';
import { PlayListToScreenBox } from './styled';

import CreatePlaylist from '../CriarPlayListPage/CriarPlayListPage';

class PlayListPage extends React.Component {
  state = {
    playLists: [],
    isedit:false,
  }

  componentDidMount() {
    this.getAllPlaylists()
  }

  getAllPlaylists = () => {
    const url = `${BASE_URL}`
    const axiosConfig = {
      headers: {
        Authorization: AuthorizationUser
      }
    }
    axios
      .get(url, axiosConfig)
      .then((res) => {
        console.log(res.data.result.list)
        this.setState({
          playLists: res.data.result.list
        })
      })
      .catch(err => console.log(err.response))
  }

  createPlaylist = () => {

 this.setState({
      isedit: !this.state.isedit
    })

    }




  render() {
    console.log('playslist', this.state.playLists)
    const playListToScreen = this.state.playLists.map(item => {
      return <PlayListToScreenBox
        key={item.name}
        onClick={() => this.props.onClickChangingPageToDetail(item.id)}
      >
        {item.name}
      </PlayListToScreenBox>
    })
    return (
      <>
        <h1>PlayListPage</h1>
        <button onClick={this.createPlaylist}>Criar PlayList</button>
        <div>{playListToScreen}</div>
        {this.createPlaylist() || <CreatePlaylist/>}
      </>
    )
  }
}
export default PlayListPage;
